targets.query:
from(bucket: "snorkel")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r["_measurement"] == "run_engine_job")
    |> filter(fn: (r) => r["_field"] == "99.9_percentile")
    |> map(fn: (r) => ({
        r with
        _field: r.queue_name + "." + r.function
    }))
    |> drop(columns: ["host", "metric_type", "queue_name", "function", "_measurement"])

from(bucket: "snorkel")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r["_measurement"] == "api_requests_all")
    |> map(fn: (r) => ({
        _time: r._time,
        _value: r._value,
        _field: r.api_name + ": " + r.method + " " + r.path + " " + r.status_code,
    }))

from(bucket: "snorkel")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r["_measurement"] == "api_requests_4xx")
    |> map(fn: (r) => ({
        _field: r.api_name + ": " + r.method + " " + r.path + " " + r.status_code,
        _time: r._time,
        _value: r._value
    }))

from(bucket: "snorkel")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r["_measurement"] == "mem" and r["_field"] == "used_percent")
    |> map(fn: (r) => ({
        r with
        _field: "mem_used"
        }))
    |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
    |> yield(name: "mean")

import "experimental/aggregate"
from(bucket: "snorkel")
|> range(start: v.timeRangeStart, stop: v.timeRangeStop)

|> filter(fn: (r) => r["_measurement"] == "memory_rss")
|> aggregate.rate(every: 2m, groupColumns: ["machine"],unit: 1s)
|> group(columns: ["_measurement"])
|> map(fn: (r) => ({
    r with
    _value: (r._value + 1.0) / (r._value + 1.0)
    })
)
|> aggregateWindow(every: v.windowPeriod, fn: sum, createEmpty: false)
|> yield(name: "mean")

from(bucket: "snorkel")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r["_measurement"] == "mem" and r["_field"] == "used_percent")
    |> map(fn: (r) => ({
        r with
        _field: "mem_used"
        }))
    |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
    |> yield(name: "mean")

from(bucket: "snorkel")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)

    |> filter(fn: (r) => r["_measurement"] == "api_requests_5xx")
    |> map(fn: (r) => ({
            _field: r.api_name + ": " + r.method + " " + r.path + " " + r.status_code,
            _time: r._time,
            _value: r._value
    }))
